class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ct = 0; int n = nums.size();
        unordered_map<long long, long long> pr;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> cr; 

            if (nums[i] == k) {
                ++ct;
            }
            cr[nums[i]] = 1;


            for (auto it = pr.begin(); it != pr.end(); ++it) {
                long long v = it->first;
                long long f = it->second;
                long long nr = v & nums[i];
                if (nr == k) {
                    ct += f;
                }
                cr[nr] += f;
            }


            pr = cr;
        }

        return ct;
    }
};
