#define ll long long 
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        ll ans=0;
        map<ll,ll>mp;
        for(int i=0;i<n;i++)
        {
            map<ll,ll>mp1;
            if(nums[i]==k)ans++;
            mp1[nums[i]]=1;
            for(auto it:mp)
            {
                ll x=(it.first)&(nums[i]);
                if(x==k)ans+=it.second;
                mp1[x]+=it.second;
            }
            mp=mp1;
        }
        return ans;
    }
};
