class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<int>> l0(n, vector<int>(33, -1));
        long long o=1;
        for(int i=0;i<n;i++){
            for(int j=0;j<=30;j++){
                if(i!=0){
                    l0[i][j]=l0[i-1][j];
                }
                if(((nums[i]>>j)&o)==0){
                    l0[i][j]=i;
                }
                // cout<<l0[i][j]<<" "<<i<<" "<<j<< endl;
            }
        }

        long long ans=0;
        for(int i=0;i<n;i++){
            int mn=i, mx=0;
            for(int j=0;j<=30;j++){
                if(((k>>j)&o)==0){
                    mn=min(mn, l0[i][j]);
                }
                else {
                    mx=max(mx, l0[i][j]+1);
                }
            }
            if(mx<=mn){
                ans+=mn-mx+1;
            }
        }
        return ans;
    }

};
