class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:

        if all([i == k for i in nums]):
            return int((1 + len(nums)) * (len(nums)/2))

        ans = 0
        bason = (1 << 25) -1

        for ea in nums:
            bason &= ea

        if bason > k:
            return 0

        bason = 0
        hits = set()
        for ea in range(len(nums)):

            print(ans, ea)
            if (nums[ea] & k) < k:
                continue
            basis = nums[ea]

            if ea in hits:
                print('huld', ea)
                continue
            deepfugg = 1


            for jh in range(ea, len(nums)):


                basis &= nums[jh]
                if basis == nums[jh] and jh != ea and (jh not in hits):
                    deepfugg += 1
                    hits.add(jh)

                if basis == k:

                    if bason > jh:
                        ans += deepfugg *(1 + bason - jh)
                        break
                    else:
                        ans += deepfugg
                        bason = jh

                if basis < k:
                    break
        return ans
