using ll = long long;
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll ans = (nums[0] == k);
        map <int, ll> dp;
        dp[nums[0]] = 1;
        for(int i = 1; i < n; i++) 
        {
            map <int, ll> curr;
            ans += (nums[i] == k);
            curr[nums[i]]++;
            for(auto &it : dp) {
                if((it.first & nums[i]) == k) {
                    ans += it.second;
                }
                curr[(it.first) & nums[i]] += it.second;
            }
            dp = curr;
        }
        return ans;
    }
};
