class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
       int n =nums.size();
       long long cnt=0;
        unordered_map<long long,long long>mp;
        for(auto it: nums){
            unordered_map<long long,long long>now;
            if(it==k)cnt++;
            now[it]=1;
            for(auto& iter:mp){
                int pval=iter.first;
                int pfreq=iter.second;
                long long newr=pval & it;
                if(newr==k){
                    cnt+=pfreq;
                }
                now[newr]+=pfreq;
            }
            mp=now;
        }
        return cnt;
    }
};
