class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

int n = nums.size();
    long long count = 0;
    std::unordered_map<int, int> freq;

    for (int i = 0; i < n; ++i) {
        std::unordered_map<int, int> new_freq;
        for (auto& [key, value] : freq) {
            int new_key = key & nums[i];
            new_freq[new_key] += value;
        }
        new_freq[nums[i]]++;

        for (auto& [key, value] : new_freq) {
            if (key == k) {
                count += value;
            }
        }

        freq = new_freq;
    }

    return count;









    }
};
