class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
    unordered_map<long long, long long> m;
    long long ans = 0;

    for (long long i = 0; i < nums.size(); ++i) {
        unordered_map<long long, long long> nm;
        for (auto &it : m) {
            long long nand = it.first & nums[i];
            nm[nand] += it.second;
        }
        nm[nums[i]]++;
        m = nm;

        if (m.find(k) != m.end()) {
            ans += m[k];
        }
    }

    return ans;
    }
};
