class Solution {
public:
    void addNum(int n, vector<int> &bits){
        for(int i=0; i<32; ++i){
            if((n >> i)&1) bits[i]++;
        }
    }
    void remNum(int n, vector<int> &bits){
        for(int i=0; i<32; ++i){
            if((n >> i)&1) bits[i]--;
        }
    }
    int val(vector<int> &bits, int sz){
        int ans = 0;
        for(int i=0; i<32; ++i){
            int sets = bits[i];
            if(sets == sz) ans += 1 << i;
        }
        return ans;
    }

    long long f(vector<int>& nums, int k){
        vector<int> bits(32);

        int n = nums.size();
        int j = 0;
        long long ans = 0;
        for(int i=0; i<n; ++i){
            addNum(nums[i], bits);
            int x = val(bits, i - j + 1 );
            while(j <= i && x < k){
                remNum(nums[j], bits);
                j++;
                x = val(bits,i-j+1);
            }
            // x = val(nums[i], i - j + 1);
            if(j<=i &&  val(bits, i - j + 1) >= k) ans += i - j + 1;
        }
        return ans;
    }

    long long countSubarrays(vector<int>& nums, int k) {
        long long a = f(nums, k);
        long long b = f(nums, k+1);
        return a - b;
    }
};
