class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        unordered_map<int, long long> pref;

        for (const auto& i : nums) {
            unordered_map<int, long long> mp;
            if(i == k) ans++;
            mp[i]++;

            for (const auto& pair : pref) {
                int curAnd = pair.first & i;
                if (curAnd == k) ans += pair.second;
                mp[curAnd] += pair.second;
            }

            pref = mp;
        }

        return ans;
    }
};

