class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
         int n = nums.size();
    long long total = 0;
    unordered_map<long long, long long> previousResult;

    for (int i = 0; i < n; ++i) {
        unordered_map<long long, long long> currentResult; 
        if (nums[i] == k) {
            ++total;
        }
        currentResult[nums[i]] = 1;

        for (auto& [value, frequency] : previousResult) {
            long long newAndResult = value & nums[i];
            if (newAndResult == k) {
                total += frequency;
            }
            currentResult[newAndResult] += frequency;
        }

        previousResult = currentResult;
    }
    return total;
    }
};
