class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        stack = []
        ans = 0
        for i in range(n):
            new = [[nums[i],1]]
            for x,c in stack:
                if x & nums[i] == new[-1][0]:
                    new[-1][1] += c
                else:
                    new.append([x & nums[i],c])
            for x,c in new:
                if x == k:
                    ans += c
            stack = new
        return ans
