class Solution {
public:
     long long countSubarrays(vector<int>& nums, int k) {
        long long cnt=0;
        unordered_map<long long, long long> prev;
        for(int i=0; i<nums.size(); i++){
            unordered_map<long long, long long> curr;
            if(nums[i]==k) cnt++;
            curr[nums[i]]++;
            for(auto &[f, s]: prev){
                long long temp=f&nums[i];
                if(temp==k) cnt+=s;
                curr[temp]+=s;
            } prev=curr;
        } return cnt;
    }
};
