class Solution {
    public long countSubarrays(int[] array, int target) {
        int size = array.length;
        long total = 0;
        Map<Long, Long> previousResults = new HashMap<>();

        for (int index = 0; index < size; ++index) {
            Map<Long, Long> currentResults = new HashMap<>();
            if (array[index] == target) {
                ++total;
            }
            currentResults.put((long) array[index], 1L);
            for (Map.Entry<Long, Long> entry : previousResults.entrySet()) {
                long value = entry.getKey();
                long frequency = entry.getValue();
                long newAndResult = value & array[index];
                if (newAndResult == target) {
                    total += frequency;
                }
                currentResults.put(newAndResult, currentResults.getOrDefault(newAndResult, 0L) + frequency);
            }
            previousResults = currentResults;
        }
        return total;
    }
}
