class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        int n=a.size();
        vector<vector<int>> fstzero(n,vector<int>(31,n));
        for(int j=0;j<31;j++)
        {
            if(!((1<<j)&a[n-1])){
                fstzero[n-1][j]=n-1;
            }
        }
        for(int i=n-2;i>=0;i--)
        {
            fstzero[i]=fstzero[i+1];
            for(int j=0;j<31;j++)
            {
                if(!((1<<j)&a[i])){
                    fstzero[i][j]=i;
                }
            }
        }
        long long ans=0;
        for(int i=0;i<n;i++)
        {
            int mn=i,mx=n;
            for(int j=0;j<31;j++)
            {
                if((1<<j)&k){
                    mx=min(fstzero[i][j],mx);
                }
                else
                {
                    mn = max(fstzero[i][j],mn);
                }
            }
            ans+=max(0,mx-mn);
        }
        return ans;
    }
};
