class Solution {
public:
    unordered_map<long long, long long> mp;
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        for (int i = 0; i < nums.size(); ++i) {
            unordered_map<long long, long long> mpp; 
            if (nums[i] == k) ans++;
            mpp[nums[i]] = 1;
            for (auto it : mp) {
                long long fans = (it.first & nums[i]);
                 mpp[fans] += it.second;
                if (fans == k)ans += it.second;
            }
            mp = mpp;
        }

        return ans;
    }
};
