class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        import sortedcontainers
        res = 0
        prev = sortedcontainers.SortedDict()
        for i, num in enumerate(nums):
            tmp = [(i, num)]
            for pv, pi in reversed(prev.items()):
                tmp.append((pi, pv & num))
            cur = sortedcontainers.SortedDict()
            for ci, cv in tmp:
                if cv in cur:
                    continue
                else:
                    cur[cv] = ci
            if k in cur:
                idx = cur.index(k)
                if idx > 0:
                    res += cur[k] - cur.values()[idx - 1]
                else:
                    res += cur[k] + 1
            prev = cur
        return res
