class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        size = len(nums)
        target = k
        total = 0
        previous_results = {}

        for index in range(size):
            current_results = {}
            if nums[index] == target:
                total += 1
            current_results[nums[index]] = 1
            for value, frequency in previous_results.items():
                new_and_result = value & nums[index]
                if new_and_result == target:
                    total += frequency
                current_results[new_and_result] = current_results.get(new_and_result, 0) + frequency

            previous_results = current_results
        return total
