class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long ans=0;
        map<long long ,long long> mp1; //use to store previous  ands
        for(int i=0;i<n;i++){
        map<long long,long long> mp2; //use to store current ands
            if(nums[i]==k) ans++;
            mp2[nums[i]]=1;
            for(auto it:mp1){
                long long nand=(it.first & nums[i]);
                if(nand==k) ans+=it.second;
                mp2[nand]+=it.second;
            }
            mp1=mp2;
        }
        return ans;
    }
};
