class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long cnt=0;
        unordered_map<long long,long long> mp;
        int i=0;
        while(i<n){
            if(nums[i]==k) cnt++;
            unordered_map<long long,long long> mp1;
            mp1[nums[i]]=1;
            unordered_map<long long,long long>::iterator it=mp.begin();
            while(it!=mp.end()){
                long long temp=it->first&nums[i];
                if(temp==k) cnt+=it->second;
                mp1[temp]+=it->second;
                it++;
            }
            mp=mp1;
            i++;
        }
        return cnt;
    }
};
