class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long, long long> mp;
        for (int i = 0; i < n; ++i)
        {
            unordered_map<long long, long long> temp;
            if (nums[i] == k)
            {
                ++ans;
            }
            temp[nums[i]] = 1;
            for (auto j : mp)
            {
                long long x = j.first & nums[i];
                if (x == k)
                {
                    ans += j.second;
                }
                temp[x] += j.second;
            }

            mp = temp;
        }
        return ans;
    }
};
