#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        ll ans = 0 ;
        map<ll, ll> mp, cur ;
        ll nd = 1 ;
        for (auto &x: a) {
            cur.clear() ;
            cur[x] = 1 ;
            if (x == k) ans ++ ;

            for (auto &y: mp) {
                nd = y.first & x ;
                cur[nd] += y.second ;
                if (nd == k)    ans += y.second ;
            }
            mp = cur ;
        }

        return ans ;
    }
};
