class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long res = 0;
        int n = nums.size();

        unordered_map<long long, long long> freqRedPrev;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> freqRedNow; 

            if (nums[i] == k) {
                ++res;
            }

            freqRedNow[nums[i]] = 1;

            for (auto& [value, key] : freqRedPrev) {
                long long newRes = value & nums[i];

                if (newRes == k) {
                    res += key;
                } else {
                    // Ok
                }
                freqRedNow[newRes] += key;
            }

            freqRedPrev = freqRedNow;
        }

        return res;
    }
};
