class Solution {
public:
    long long rohit138(vector<int>& gsk, int k){
        int target=k;
    int size = gsk.size();
        long long total = 0;
        unordered_map<long long, long long> pr;
        for (int index = 0; index < size; ++index) {
            unordered_map<long long, long long> cur; 
            if (gsk[index] == target) {
                total++;
            }
            cur[gsk[index]] = 1;
            for (auto& [value, frequency] : pr) {
                long long newAndResult = value & gsk[index];
                if (newAndResult == target) {
                    total += frequency;
                }
                cur[newAndResult] += frequency;
            }

            pr = cur;
        }
        return total;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        return rohit138(nums,k);
    }
};
