class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int size = nums.size();
        long long ans = 0;
        map <long long, long long> m;
        for(auto i: nums){
            map <long long, long long> n;
            if(i == k) ++ans;
            n[i]=1;
            for(auto j: m){
                long long nn = (j.first & i);
                if(nn == k){
                    ans+= j.second;
                }
                n[nn] += j.second;
            }
            m = n;
        }
        return ans;
    }
};
