class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long, long long>mp;
        for(int i=0; i<n; i++){
            unordered_map<long long, long long> curr;
            if(nums[i] == k){
                ans++;
            }
            curr[nums[i]] = 1;
            for(auto & [value, freq]: mp){
                long long newResult = value & nums[i];
                if(newResult == k){
                    ans += freq;
                }
                curr[newResult] += freq;
            }
            mp = curr;
        }
        return ans;
    }
};
