class Solution {
public:
    long long countSubarrays(vector<int>& v, int target) {
        int n = v.size();
        long long c = 0;
        unordered_map<long long, long long> mp1;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> mp2; 
            if (v[i] == target) {
                ++c;
            }
            mp2[v[i]] = 1;

            for (auto& [value, frequency] : mp1) {

                long long newAndResult = value & v[i];

                if (newAndResult == target) {
                    c += frequency;
                }
                mp2[newAndResult] += frequency;
            }

            mp1 = mp2;
        }
        return c;
    }
};
