class Solution {
public:
    long long countSubarrays(vector<int>& nums, int target) {
        int n = nums.size();
        long long count = 0;
        unordered_map<long long, long long> prevAndResults;
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currentAndResults;
            if (nums[i] == target) {
                ++count;
            }
            currentAndResults[nums[i]] = 1;
            for (auto& [value, frequency] : prevAndResults) {
                long long newAndResult = value & nums[i];
                if (newAndResult == target) {
                    count += frequency;
                }
                currentAndResults[newAndResult] += frequency;
            }
            prevAndResults = currentAndResults;
        }
        return count;
    }
};
