class Solution {
public:
    long long countSubarrays(vector<int>& nums, int target) {
        int n = size(nums);
        long long  ans= 0;
        unordered_map<long long,long long>mp;
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long>currmp; 
            if (nums[i] == target) {
                ans++;
            }
            currmp[nums[i]] = 1;
            for (auto& it :mp) {
                long long temp = it.first & nums[i];
                if (temp == target) {
                    ans += it.second;
                }
                currmp[temp] +=it.second;
            }
            mp = currmp;
        }

        return ans;
    }
};
