using LL = long long;
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        LL ans = 0;
        unordered_map<int, LL> andMap;

        for (int& num : nums) {
            unordered_map<int, LL> newMap;

            andMap[INT_MAX]++;

            for (auto& [key, val] : andMap) {
                int newAnd = key & num;
                if (newAnd >= k) {
                    newMap[newAnd] += val;
                }
            }

            ans += newMap[k];

            andMap = newMap;
        }

        return ans;
    }
};
