class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long ans=0;
        map<long long,long long>m[n];
        m[0][nums[0]]++;
        for(int i=1;i<n;i++){
            for(auto x:m[i-1]){
                m[i][x.first&nums[i]]+=x.second;
            }
            m[i][nums[i]]++;
        }
        for(int i=0;i<n;i++){
            ans+=m[i][k];
        }
        return ans;
    }
};
