class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int m = nums.size();
        long long total = 0;
        unordered_map<long long, long long> a;//prev results:
        for (int i = 0; i < m; i++) {
            unordered_map<long long, long long> b;//curr results: 
            if (nums[i] == k) {
                total++;
            }
            b[nums[i]] = 1;
            for (auto& [value, frequency] : a) {
                long long newAndResult = value & nums[i];
                if (newAndResult == k) {
                    total=total+ frequency;
                }
                b[newAndResult] =b[newAndResult]+ frequency;
            }

            a = b;
        }
        return total;
    }
};
