class Solution:
    def logTrick(self,a,op):
        d = defaultdict(int)#每个可能的区间op值，以及对应的个数
        s = [] #开头，结尾，左闭右开，值
        for i, x in enumerate(a):
            for v in s:
                v[2] = op(v[2],x)
            s.append([i,i+1,x]) #如果写前面，或放0，and放自己，不同函数不同，不如直接最后加
            k = 0
            for v in s[1:]: #因为单调序列所以可以inplace去重
                if s[k][2] == v[2]:
                    s[k][1] = v[1]
                else:
                    k += 1
                    s[k] = v
            s = s[:k+1]
            for v in s:
                d[v[2]]+=v[1]-v[0]
        return d

    def countSubarrays(self, nums: List[int], k: int) -> int:
        d = self.logTrick(nums,and_)
        z = 0
        for x,y in d.items():
            if x == k:
                z += y
        return z
