using namespace std;

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long result = 0;
        std::unordered_map<long long, long long> prevResults;

        for (int i = 0; i < n; ++i) {
            std::unordered_map<long long, long long> currResults;
            if (nums[i] == k) {
                ++result;
            }
            currResults[nums[i]] = 1;

            for (auto& [val, freq] : prevResults) {
                long long newAnd = val & nums[i];
                if (newAnd == k) {
                    result += freq;
                }
                currResults[newAnd] += freq;
            }

            prevResults = currResults;
        }

        return result;
    }
};

