class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long res=0;
        unordered_map<long long,long long>m1;
        for(int i=0;i<n;i++){
            unordered_map<long long,long long>m2;
            if(nums[i]==k){
                res++;
            }
            m2[nums[i]]=1;
            for(auto &it:m1){
                long long r=it.first&nums[i];
                if(r==k){
                    res+=it.second;
                }
                m2[r]+=it.second;
            }
            m1=m2;
        }
        return res;
    }
};
