class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        res = 0
        a = []
        for i in range(30):
            p = 2 ** i
            if k & p == 0:
                a.append(p)
        m = len(a)
        d = {}
        start = -1
        for i, x in enumerate(nums):
            if x & k == k:
                if start == -1:
                    start = i
                for p in a:
                    if x & p == 0:
                        d[p] = i
                if len(d) == m:
                    res += min(d.values()) - start + 1
            else:
                start = -1
                d = {}
        return res
