#define Fast ios_base::sync_with_stdio(false), cin.tie(NULL), cout.tie(NULL);

class Solution {
public:
    long long countSubarrays(vector<int>& array, int k) {
      int size = array.size();
        long long int ans = 0;
        unordered_map<long long int, long long int> prev;
        for (int ind = 0; ind < size; ind++) {
            unordered_map<long long int, long long int> curr; 
            if (array[ind] == k) {
                ++ans;
            }
            curr[array[ind]] = 1;
            for (auto& [value, frequency] : prev) {
                long long res = value & array[ind];
                if (res == k) {
                    ans += frequency;
                }
                curr[res] += frequency;
            }

            prev = curr;
        }
        return ans;
    }

};
