class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<ll,ll>dp;
        ll ans=0;
        rep(i,0,nums.size()){
            if(nums[i]==k){
                ans++;
            }
            map<ll,ll> ndp;
            for(auto it:dp){
                if((it.ff&nums[i])==k){
                    ans+=it.ss;
                }
                if((it.ff&nums[i])>=k){
                    ndp[(it.ff&nums[i])]+=it.ss;
                }
            }
            ndp[nums[i]]++;
            dp=ndp;
        }
        return ans;
    }
};
