class Solution {
public:
    using ll = long long;
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<int, int> andCount;
        long long ans = 0;

        for (int num : nums) {
            unordered_map<int, int> newAndCount;

            if (num == k) {
                ans++;
            }

            for (auto &[andValue, count] : andCount) {
                int newAnd = andValue & num;
                if (newAnd == k) {
                    ans += count;
                }
                newAndCount[newAnd] += count;
            }

            newAndCount[num]++;
            andCount = newAndCount;
        }

        return ans;
    }
};
