class Solution {
public:
    typedef long long ll;
    long long solve(vector<int>& nums, int k)
    {
        int n = nums.size();
        ll cnt = 0;
        unordered_map<ll, ll> pr;

         for (int i = 0; i < n; ++i) 
         {
             unordered_map<ll, ll> cr; 
             if (nums[i] == k) 
             {
                 cnt++;
             }
             cr[nums[i]] = 1; 
             for (auto it = pr.begin(); it != pr.end(); ++it) 
             {
                 ll a = it->first;
                 ll b = it->second;

                 ll res = a & nums[i];
                 if (res == k) 
                 {
                     cnt += b;
                 }
                 cr[res] += b;
             }
             pr = cr;
         }

         return cnt;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        return solve(nums, k);
    }
};
