class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<long long, long long> a;
        int n = nums.size();
        long long ans = 0;
        for (int i = 0; i < n; ++i) {
            map<long long, long long> b; 
            if (nums[i] == k) ans++;
            b[nums[i]] = 1;
            for (auto it: a) {
                long long c = it.first & nums[i];
                if (c == k) {
                    ans += it.second;
                }
                b[c] += it.second;
            }

            a = b;
        }
        return ans;

    }
};
