class Solution {
public:
    long long countSubarrays(vector<int>& arr, int target) {
        int size = arr.size();
        long long total = 0;
        unordered_map<long long, long long> previousSubarrays;

        for (int index = 0; index < size; ++index) {
            unordered_map<long long, long long> currentSubarrays; 

            if (arr[index] == target) {
                ++total;
            }

            currentSubarrays[arr[index]] = 1;

            for (auto& [value, frequency] : previousSubarrays) {
                long long newAndResult = value & arr[index];

                if (newAndResult == target) {
                    total += frequency;
                }

                currentSubarrays[newAndResult] += frequency;
            }

            previousSubarrays = currentSubarrays;
        }

        return total;
    }
};
