class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long tcnt = 0;

        Map<Long, Long> mpp = new HashMap<>();

        for (int i = 0; i < n; ++i) {
            Map<Long, Long> cur = new HashMap<>(); 

            if (nums[i] == k) {
                ++tcnt;
            }
            cur.put((long) nums[i], 1L);

            for (Map.Entry<Long, Long> entry : mpp.entrySet()) {
                long val = entry.getKey();
                long freq = entry.getValue();

                long newres = val & nums[i];
                if (newres == k) {
                    tcnt += freq;
                }
                cur.put(newres, cur.getOrDefault(newres, 0L) + freq);
            }

            mpp = cur;
        }

        return tcnt;
    }
}
