class Solution {
public:
 using ll=long long;
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        ll res=0;
        unordered_map<ll,ll>prer;
        for(int i=0;i<n;i++){
            unordered_map<ll,ll>cr;
            if(nums[i]==k)res++;
            cr[nums[i]]=true;
            for(auto&[ele,freq]:prer){
                ll newr=ele&nums[i];
                if(newr==k)res+=freq;
                cr[newr]+=freq;
            }
        prer=cr;
        }
        return res;
    }
};
