/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var countSubarrays = function(nums, k) {
        let size = nums.length;
    let total = 0;
    let previousResults = {};

    for (let index = 0; index < size; ++index) {
        let currentResults = {};

        if (nums[index] == k) {
            total++;
        }
        currentResults[nums[index]] = 1;

        for (let value in previousResults) {
            let frequency = previousResults[value];
            let newAndResult = value & nums[index];

            if (newAndResult == k) {
                total += frequency;
            }

            if (currentResults[newAndResult] === undefined) {
                currentResults[newAndResult] = 0;
            }
            currentResults[newAndResult] += frequency;
        }

        previousResults = currentResults;
    }

    return total;
};
