class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        int close = INT_MAX;
        map<int,int> a,b;
        long long ans = 0,prev=0,count=0;
        for(int i=0;i<n;i++) {
            b.clear();
            for(auto &itr : a) {
                if((itr.first&nums[i])==k) {
                    ans += itr.second;
                }
                b[itr.first&nums[i]] += itr.second;
            }
            b[nums[i]]++;
            if(nums[i]==k) ans++;
            swap(a,b);
        }
        return ans;
    }
};
