class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        unordered_map<long long, long long> pc;
        int n = array.size();
        long long ans = 0;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> cpc; 
            cpc[array[i]] = 1;
            for (auto& [value, freq] : pc) {
                long long res = value & array[i];
                if (res == target) {
                    ans += freq;
                }
                cpc[res] += freq;
            }
            if (array[i] == target) ++ans;
            pc = cpc;
        }

        return ans;
    }
};
