class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long res = 0;
        map<int,int> pm;
        for (int i=0;i<n;i++) {
            map<int,int> cm;
            if (nums[i]==k) {
                res++;
            }
            cm[nums[i]]=1;
            for(auto it:pm) {
                int temp=(it.first)&nums[i];
                if (temp==k) {
                    res+=(it.second);
                }
                cm[temp]+=(it.second);
            }
            pm = cm;
        }
        return res;
    }
};
