class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long totalSubarrays = 0;

        unordered_map<long long, long long> andResultsMap;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> newAndResults;

            // Check if current element is equal to k
            if (nums[i] == k) {
                ++totalSubarrays;
            }
            newAndResults[nums[i]] = 1;
            for (const auto& entry : andResultsMap) {
                long long newAndResult = entry.first & nums[i];
                if (newAndResult == k) {
                    totalSubarrays += entry.second;
                }
                newAndResults[newAndResult] += entry.second;
            }
            andResultsMap = std::move(newAndResults);
        }

        return totalSubarrays;
    }
};
