class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;

        map<long long, long long> preAND;

        for (int i = 0; i < n; ++i) {
            map<long long, long long> currAND; 

            if (nums[i] == k)count++;

            currAND[nums[i]] = 1;

            for (auto& [val, freq] : preAND) {
                long long newAndResult = val & nums[i];
                if (newAndResult == k) {
                    count += freq;
                }
                currAND[newAndResult] += freq;
            }

            preAND = currAND;
        }

        return count;
    }
};
